mapper.select:

select *
from (
    select 
    rownum, user_id, title, content, love_cnt, created_at
    from 
    (select * from board_tb order by love_cnt desc)
    where rownum <= #{#end_check}
)
where rownum > #{begin_check}

mapper.update:
update board_tb
   set title = #{title},
       content = #{content}
 where id = #{id}

update board_tb
   set love_cnt = #{loveCnt}
 where id = #{id}
